CREATE OR REPLACE FUNCTION obter_score_idade_ep_md (qt_idade_p bigint ) RETURNS bigint AS $body$
DECLARE


   qt_pt_idade_w bigint := 0;

BEGIN
	--- Inicio MD1
	-- idade
	if (qt_idade_p IS NOT NULL AND qt_idade_p::text <> '') then
		if (qt_idade_p	<=	60) then
			qt_pt_idade_w	:=	1;
		elsif (qt_idade_p	>=	71) then
			qt_pt_idade_w	:=	4;
		else
			qt_pt_idade_w	:=	2;
		end if;
	end if;
	--- Fim MD1
    RETURN coalesce(qt_pt_idade_w,0);
   
END;
