CREATE OR REPLACE FUNCTION obter_score_mst_waterlow_md (ie_weight_loss_p bigint, qt_weight_loss_p bigint, ie_apetite_loss_p text ) RETURNS bigint AS $body$
DECLARE


   qt_score_mst_w   bigint := 0;

BEGIN
  IF (ie_weight_loss_p = 2) THEN
    qt_score_mst_w  := qt_score_mst_w + 2;
