  elsif (ie_weight_loss_p = 1 AND qt_weight_loss_p = 5 ) THEN
    qt_score_mst_w  := 2;
  END IF;

  IF (ie_apetite_loss_p = 'S') THEN
    qt_score_mst_w  := qt_score_mst_w + 1;
  END IF;

  RETURN coalesce(qt_score_mst_w,0);
   
END;
