CREATE OR REPLACE FUNCTION obter_score_perd_sang_ep_md (qt_perda_sangue_p bigint ) RETURNS bigint AS $body$
DECLARE


   qt_pt_perda_sangue_w	smallint := 0;

BEGIN
	--- Inicio MD11
	-- perda sangue
	if (qt_perda_sangue_p IS NOT NULL AND qt_perda_sangue_p::text <> '') then
		if (qt_perda_sangue_p	<=	100) then
			qt_pt_perda_sangue_w	:=	1;
