			qt_pt_perda_sangue_w	:=	2;
		elsif (qt_perda_sangue_p	<=	999) then
			qt_pt_perda_sangue_w	:=	4;
		else
			qt_pt_perda_sangue_w	:=	8;
		end if;
	end if;
	--- Fim MD11
    RETURN coalesce(qt_pt_perda_sangue_w,0);
   
END;
