CREATE OR REPLACE FUNCTION obter_score_potassio_ep_md (qt_potassio_p bigint ) RETURNS bigint AS $body$
DECLARE


   qt_pt_potassio_w	smallint := 0;

BEGIN
	--- Inicio MD7
	-- potassio
	if (qt_potassio_p IS NOT NULL AND qt_potassio_p::text <> '') then
		if (qt_potassio_p	<=	2.8) then
			qt_pt_potassio_w	:=	8;
		elsif (qt_potassio_p	<=	3.1) then
			qt_pt_potassio_w	:=	4;
		elsif (qt_potassio_p	<=	3.4) then
			qt_pt_potassio_w	:=	2;
		elsif (qt_potassio_p	<=	5.0) then
			qt_pt_potassio_w	:=	1;
		elsif (qt_potassio_p	<=	5.3) then
			qt_pt_potassio_w	:=	2;
		elsif (qt_potassio_p	<=	5.9) then
			qt_pt_potassio_w	:=	4;
		else
			qt_pt_potassio_w	:=	8;
		end if;
	end if;
	--- Fim MD7
    RETURN coalesce(qt_pt_potassio_w,0);
   
END;
