CREATE OR REPLACE FUNCTION obter_score_temp_apach_md (qt_temp_p bigint, qt_temp_max_p bigint ) RETURNS bigint AS $body$
DECLARE


   qt_pt_temperatura_min_w	smallint;
   qt_pt_temperatura_max_w	smallint;
   qt_pt_temperatura_w	    smallint;

BEGIN
	--- Inicio MD3
	-- temperatura 
	if (qt_temp_p	<=	32.9) then
		qt_pt_temperatura_min_w	:=	20;
	elsif (qt_temp_p	<=	33.4) then
		qt_pt_temperatura_min_w	:=	16;
	elsif (qt_temp_p	<=	33.9) then
		qt_pt_temperatura_min_w	:=	13;
	elsif (qt_temp_p	<=	34.9) then
		qt_pt_temperatura_min_w	:=	8;
	elsif (qt_temp_p	<=	35.9) then
		qt_pt_temperatura_min_w	:=	2;
	elsif (qt_temp_p	<=	39.9) then
		qt_pt_temperatura_min_w	:=	0;
	else
		qt_pt_temperatura_min_w	:=	4;
	end if;

	if (qt_temp_max_p	<=	32.9) then
		qt_pt_temperatura_max_w	:=	20;
	elsif (qt_temp_max_p	<=	33.4) then
		qt_pt_temperatura_max_w	:=	16;
	elsif (qt_temp_max_p	<=	33.9) then
		qt_pt_temperatura_max_w	:=	13;
	elsif (qt_temp_max_p	<=	34.9) then
		qt_pt_temperatura_max_w	:=	8;
	elsif (qt_temp_max_p	<=	35.9) then
		qt_pt_temperatura_max_w	:=	2;
	elsif (qt_temp_max_p	<=	39.9) then
		qt_pt_temperatura_max_w	:=	0;
	else
		qt_pt_temperatura_max_w	:=	4;
	end if;
	--- Fim MD3
	if (qt_pt_temperatura_max_w > qt_pt_temperatura_min_w) then
		qt_pt_temperatura_w	:=	qt_pt_temperatura_max_w;
	else
		qt_pt_temperatura_w	:=	qt_pt_temperatura_min_w;
	end if;

    return qt_pt_temperatura_w;

END;
