CREATE OR REPLACE FUNCTION obter_scor_glic_apach_md (qt_glicose_p bigint, qt_glicose_max_p bigint ) RETURNS bigint AS $body$
DECLARE


	qt_pt_glicose_w			smallint;
	qt_pt_glicose_min_w		smallint;
	qt_pt_glicose_max_w		smallint;

BEGIN
	--- Inicio MD15
	-- Glicose 
	if (qt_glicose_p	<=	39) then
		qt_pt_glicose_min_w	:=	8;
	elsif (qt_glicose_p	<=	59) then
		qt_pt_glicose_min_w	:=	9;
	elsif (qt_glicose_p	<=	199) then
		qt_pt_glicose_min_w	:=	0;
	elsif (qt_glicose_p	<=	349) then
		qt_pt_glicose_min_w	:=	3;
	else
		qt_pt_glicose_min_w	:=	5;
	end if;

	if (qt_glicose_max_p	<=	39) then
		qt_pt_glicose_max_w	:=	8;
	elsif (qt_glicose_max_p	<=	59) then
		qt_pt_glicose_max_w	:=	9;
	elsif (qt_glicose_max_p	<=	199) then
		qt_pt_glicose_max_w	:=	0;
	elsif (qt_glicose_max_p	<=	349) then
		qt_pt_glicose_max_w	:=	3;
	else
		qt_pt_glicose_max_w	:=	5;
	end if;
	--- Fim MD15
	
	if (qt_pt_glicose_max_w > qt_pt_glicose_min_w) then
		qt_pt_glicose_w	:=	qt_pt_glicose_max_w;
	else
		qt_pt_glicose_w	:=	qt_pt_glicose_min_w;
	end if;

    RETURN qt_pt_glicose_w;
   
END;
