CREATE OR REPLACE FUNCTION obter_scor_sodio_apach_md (qt_na_p bigint, qt_na_max_p bigint ) RETURNS bigint AS $body$
DECLARE


	qt_pt_na_w		smallint;
	qt_pt_na_min_w	smallint;
	qt_pt_na_max_w	smallint;

BEGIN
	--- Inicio MD9
	-- Sodium 
	if (qt_na_p	<=	119) then
		qt_pt_na_min_w	:= 3;
	elsif (qt_na_p	<=	134) then
		qt_pt_na_min_w	:=	2;
	elsif (qt_na_p	<=	154) then
		qt_pt_na_min_w	:=	0;
	else
		qt_pt_na_min_w	:=	4;
	end if;

	if (qt_na_max_p	<=	119) then
		qt_pt_na_max_w	:= 3;
	elsif (qt_na_max_p	<=	134) then
		qt_pt_na_max_w	:=	2;
	elsif (qt_na_max_p	<=	154) then
		qt_pt_na_max_w	:=	0;
	else
		qt_pt_na_max_w	:=	4;
	end if;

	if (qt_pt_na_max_w > qt_pt_na_min_w) then
		qt_pt_na_w	:=	qt_pt_na_max_w;
	else
		qt_pt_na_w	:=	qt_pt_na_min_w;
	end if;

	return qt_pt_na_w;
	--- Fim MD9
END;
