CREATE OR REPLACE FUNCTION obter_scrore_escala_tev_md ( ie_clinico_cirurgico_p text, ie_risco_p text ) RETURNS bigint AS $body$
BEGIN

	if (ie_clinico_cirurgico_p = 'C') then
	  if (ie_risco_p = 'B') then
	    return 1;
	  elsif (ie_risco_p = 'M') then
	    return 2;
	  elsif (ie_risco_p = 'A') then
	    return 3;
	  end if;
	else
	  if (ie_risco_p = 'B') then
	    return 4;
	  elsif (ie_risco_p = 'M') then
	    return 5;
	  elsif (ie_risco_p = 'A') then
	    return 6;
	  end if;
	end if;

end;
$body$
