SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_segmento_material ( cd_material_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
nr_seq_familia_w			bigint;
nr_seq_segmento_w		bigint	:= 0;
ie_curva_abc_w			varchar(1);

c01 CURSOR FOR
SELECT	nr_seq_segmento
from	segmento_compras_estrut a,
	segmento_compras b
where	b.nr_sequencia = a.nr_seq_segmento
and	coalesce(b.cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	coalesce(cd_material, cd_material_p)			= cd_material_p
and	((coalesce(nr_seq_familia, nr_seq_familia_w)		= nr_seq_familia_w) or (coalesce(nr_seq_familia::text, '') = ''))
and	((coalesce(a.ie_curva_abc,ie_curva_abc_w)		= ie_curva_abc_w) or (a.ie_curva_abc = 'T'))
order by	coalesce(cd_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_grupo_material,0),
	coalesce(nr_seq_familia,0),
	coalesce(cd_estabelecimento,0);


BEGIN

if (coalesce(cd_material_p,0) > 0) then
	begin

	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		nr_seq_familia
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w,
		nr_seq_familia_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	ie_curva_abc_w	:= SUBSTR(Obter_Curva_ABC_Estab(cd_estabelecimento_p, cd_material_p, 'N',clock_timestamp()),1,1);

	open c01;
	loop
	fetch c01 into
		nr_seq_segmento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;

	if (nr_seq_segmento_w > 0) then
		begin

		if (ie_opcao_p = 'C') then
			ds_retorno_w	:= to_char(nr_seq_segmento_w);
		else
			select	ds_segmento
			into STRICT	ds_retorno_w
			from	segmento_compras
			where	nr_sequencia = nr_seq_segmento_w;
		end if;

		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
