CREATE OR REPLACE FUNCTION obter_segundo_entre_datas ( dt_inicial_p timestamp, dt_final_p timestamp, qt_min_ref_p bigint) RETURNS bigint AS $body$
DECLARE


qt_minuto_w			bigint;
qt_min_ref_w		smallint;


BEGIN

IF (coalesce(dt_inicial_p::text, '') = '') OR (coalesce(dt_final_p::text, '') = '') THEN
	qt_minuto_w		:= 0;
ELSE
	BEGIN
	qt_min_ref_w	:= coalesce(qt_min_ref_p,1);
	qt_minuto_w		:= ROUND((dt_final_p - dt_inicial_p) * 86400);
	qt_minuto_w		:= ROUND(qt_minuto_w / qt_min_ref_w) * qt_min_ref_w;
	END;
END IF;

RETURN qt_minuto_w;

END;
$body$
