CREATE OR REPLACE FUNCTION obter_select_concatenado ( ds_comando_p text, ds_parametro_p text, ie_separador_p text) RETURNS varchar AS $body$
DECLARE


/*

			NÃƒO UTILIZAR ESTA FUNCTION
		SUBSTITUIDA POR OBTER_SELECT_CONCATENADO BV

*/
ds_resultado_w			varchar(2000);
vl_retorno_w			varchar(255);
c001				integer;
retorno_w			integer;

ie_parametro_w			smallint;
vl_parametro_w			varchar(255);
ds_comando_w			varchar(2000);
ds_parametro_w			varchar(255);


BEGIN

ie_parametro_w	:= 1;
ds_parametro_w	:= ds_parametro_p;
ds_comando_w	:= ds_comando_p;
while position(':' in ds_comando_w) > 0 loop
	vl_parametro_w := substr(ds_parametro_w, 1, position(';' in ds_parametro_w) - 1);
	ds_parametro_w := replace(ds_parametro_w, vl_parametro_w||';','');
	ds_comando_w := replace(ds_comando_w, ':'||ie_parametro_w, coalesce(vl_parametro_w,ds_parametro_w));
	ie_parametro_w := ie_parametro_w + 1;
end loop;

begin
C001 := DBMS_SQL.OPEN_CURSOR;
DBMS_SQL.PARSE(C001, ds_comando_w, dbms_sql.Native);
DBMS_SQL.DEFINE_COLUMN(C001, 1, vl_retorno_w, 255);
retorno_w := DBMS_SQL.execute(c001);
ds_resultado_w	:= '';
retorno_w := DBMS_SQL.fetch_rows(c001);
while retorno_w <> 0 loop
	DBMS_SQL.COLUMN_VALUE(C001, 1, vl_retorno_w );
	ds_resultado_w := ds_resultado_w || vl_retorno_w || ie_separador_p;
	retorno_w := DBMS_SQL.fetch_rows(c001);
end loop;
DBMS_SQL.CLOSE_CURSOR(C001);
exception
	when others then
		DBMS_SQL.CLOSE_CURSOR(C001);
end;

return ds_resultado_w;
END;
$body$
