CREATE OR REPLACE FUNCTION obter_semana_dia_agecons (dt_referencia_p timestamp, ie_dia_semana_p bigint) RETURNS bigint AS $body$
DECLARE


dt_inicial_w	timestamp := PKG_DATE_UTILS.start_of(dt_referencia_p,'month',0);
dt_final_w	timestamp := PKG_DATE_UTILS.END_OF(dt_referencia_p, 'MONTH', 0);
nr_dia_semana_w	bigint:= 1;
qt_dia_w	varchar(1);
ie_dia_semana_w		varchar(1);

BEGIN

while dt_inicial_w <=dt_final_w loop
	begin
	if (dt_inicial_w	= PKG_DATE_UTILS.start_of(dt_referencia_p, 'dd', 0)) then
		exit;
	end if;
	
	qt_dia_w	:= '7';
	ie_dia_semana_w	:= pkg_date_utils.get_WeekDay(dt_inicial_w);
		
	if (ie_dia_semana_p 	= 9) and (ie_dia_semana_w	= qt_dia_w) then
		nr_dia_semana_w	:= nr_dia_semana_w +1;
	elsif (ie_dia_semana_w	= ie_dia_semana_p) then
		nr_dia_semana_w	:= nr_dia_semana_w +1;
	end if;
	
	dt_inicial_w	:= dt_inicial_w +1;

	end;
end loop;

return	nr_dia_semana_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
