CREATE OR REPLACE FUNCTION obter_semana_dia (dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_inicial_w	timestamp := trunc(dt_referencia_p,'Month');
dt_final_w	timestamp := pkg_date_utils.get_datetime(pkg_date_utils.end_of(dt_referencia_p, 'MONTH', 0), coalesce(dt_referencia_p, PKG_DATE_UTILS.GET_TIME('00:00:00')));
nr_dia_semana_w	bigint:= 1;

BEGIN

while dt_inicial_w <=dt_final_w loop
	begin
	if (dt_inicial_w	= trunc(dt_referencia_p)) then
		exit;
	end if;
	if (pkg_date_utils.get_WeekDay(dt_inicial_w)	= '7') then
		nr_dia_semana_w	:= nr_dia_semana_w +1;
	end if;
	dt_inicial_w	:= dt_inicial_w +1;

	end;
end loop;

return	nr_dia_semana_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
