CREATE OR REPLACE FUNCTION obter_semana_mes ( dt_parametro_p timestamp) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_semana_mes_w			varchar(255);
ie_inicio_semana_w		bigint;
ie_dia_w			bigint;


BEGIN
ie_inicio_semana_w	:= pkg_date_utils.get_WeekDay(trunc(dt_parametro_p,'mm'))-1;
ie_dia_w		:= (to_char(dt_parametro_p,'dd'))::numeric;

if	(ie_dia_w <= (7 - ie_inicio_semana_w)) then
	ie_semana_mes_w	:= '1';
elsif	(ie_dia_w <= (14 - ie_inicio_semana_w)) then
	ie_semana_mes_w	:= '2';
elsif	(ie_dia_w <= (21 - ie_inicio_semana_w)) then
	ie_semana_mes_w	:= '3';
elsif	(ie_dia_w <= (28 - ie_inicio_semana_w)) then
	ie_semana_mes_w	:= '4';
elsif	(ie_dia_w <= (35 - ie_inicio_semana_w)) then
	ie_semana_mes_w	:= '5';
elsif	(ie_dia_w <= (42 - ie_inicio_semana_w)) then
	ie_semana_mes_w	:= '6';
end if;

return ie_semana_mes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
