CREATE OR REPLACE FUNCTION obter_semestre_ano ( dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_referencia_w		bigint;
ds_retorno_w		varchar(2);


BEGIN

select	(substr(dt_referencia_p,4,2))::numeric  dt_ref
into STRICT	dt_referencia_w
;

if (dt_referencia_w <= 06) then
	ds_retorno_w := '01';
else
	ds_retorno_w := '02';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
