CREATE OR REPLACE FUNCTION obter_senhas_conta (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


cd_senha_w	varchar(20);
ds_retorno_w	varchar(2000)	:= '';

C01 CURSOR FOR
	SELECT	cd_senha
	from (
		SELECT	a.cd_senha
		from	procedimento_paciente a
		where	a.nr_interno_conta = nr_interno_conta_p
		and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
		and	(a.cd_senha IS NOT NULL AND a.cd_senha::text <> '')
		
union all

		select	a.cd_senha
		from	material_atend_paciente a
		where	a.nr_interno_conta = nr_interno_conta_p
		and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
		and	(a.cd_senha IS NOT NULL AND a.cd_senha::text <> '')
		) alias4
	group by	cd_senha
	order by	cd_senha;


BEGIN

if (coalesce(nr_interno_conta_p,0) <> 0) then

	open C01;
	loop
	fetch C01 into
		cd_senha_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= cd_senha_w;
		else
			ds_retorno_w	:= ds_retorno_w || ',' || cd_senha_w;
		end if;

		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
