SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_senha_autorizacao ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(25);
cd_senha_w		varchar(25);


BEGIN



select	max(a.cd_senha)
into STRICT	cd_senha_w
from	autorizacao_convenio a
where	(a.cd_senha IS NOT NULL AND a.cd_senha::text <> '')
and	a.nr_atendimento	= nr_atendimento_p
and	a.nr_sequencia	= (SELECT 	min(y.nr_sequencia)
			     from	autorizacao_convenio y
			     where y.nr_atendimento = nr_atendimento_p
			     and	(y.cd_senha IS NOT NULL AND y.cd_senha::text <> ''));

if (ie_opcao_p = 'P') then
	ds_retorno_w := cd_senha_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
