CREATE OR REPLACE FUNCTION obter_senha_autoriz_proced ( nr_prescricao_p bigint, nr_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_senha_w			varchar(20);
nr_seq_autorizacao_w		bigint;
cd_procedimento_principal_w	bigint;
ie_origem_proced_w		bigint;
ds_retorno_w			varchar(20);
ie_existe_w			bigint;


C01 CURSOR FOR
SELECT	a.cd_senha,
	a.nr_seq_autorizacao,
	a.cd_procedimento_principal,
	a.ie_origem_proced
from	autorizacao_convenio a
where	a.nr_atendimento	= nr_atendimento_p
and	a.nr_prescricao		= nr_prescricao_p;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_senha_w,
	nr_seq_autorizacao_w,
	cd_procedimento_principal_w,
	ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	if (cd_procedimento_principal_w = cd_procedimento_p) and (ie_origem_proced_w = ie_origem_proced_p) then
		ds_retorno_w	:= ds_senha_w;
		exit;
	end if;

	select	count(*)
	into STRICT	ie_existe_w
	from	procedimento_autorizado
	where	nr_atendimento		= nr_atendimento_p
	and	nr_seq_autorizacao	= nr_seq_autorizacao_w
	and	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;

	if (ie_existe_w > 0) then
		ds_retorno_w	:= ds_senha_w;
		exit;
	end if;
END LOOP;
CLOSE C01;



RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
