CREATE OR REPLACE FUNCTION obter_senha_gerada_agendas ( CD_AGENDA_P bigint, NR_SEQUENCIA_P bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_pac_senha_fila_w bigint;
ds_retorno_w		varchar(100);


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '' AND nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

   SELECT MAX(NR_SEQ_PAC_SENHA_FILA)
   INTO STRICT nr_seq_pac_senha_fila_w
   FROM (SELECT NR_SEQ_PAC_SENHA_FILA
           FROM AGENDA_PACIENTE WHERE CD_AGENDA = CD_AGENDA_P AND NR_SEQUENCIA = NR_SEQUENCIA_P

UNION ALL

       SELECT NR_SEQ_PAC_SENHA_FILA FROM AGENDA_CONSULTA WHERE CD_AGENDA = CD_AGENDA_P AND NR_SEQUENCIA = NR_SEQUENCIA_P) alias2;

ds_retorno_w := SubStr(obter_dados_senha(nr_seq_pac_senha_fila_w, 'LC'),1,10);	
end if;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
