CREATE OR REPLACE FUNCTION obter_senha_paciente_quimio (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);

 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	max(ds_senha_qmatic || ' - ' || substr(obter_iniciais_nome(cd_pessoa_fisica, null),1,25)) 
	into STRICT	ds_retorno_w 
	from 	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
