CREATE OR REPLACE FUNCTION obter_senha_pf (dt_nascimento_p timestamp default null) RETURNS varchar AS $body$
DECLARE


ie_regra_w	varchar(3);
ds_senha_w	varchar(20);


BEGIN
/* obter valor par√¢metro */

select	coalesce(coalesce(vl_parametro, vl_parametro_padrao),'XX')
into STRICT	ie_regra_w
from	funcao_parametro
where	cd_funcao	= 0
and	nr_sequencia	= 65;

/* gerar senha regra */

if (ie_regra_w = 'NS') then
	select	to_char(nextval('atendimento_senha_seq'))
	into STRICT	ds_senha_w
	;
elsif (ie_regra_w = 'NA') then
	ds_senha_w	:= obter_numero_randomico;
elsif (ie_regra_w = 'LN') then
	ds_senha_w	:= obter_senha_letra_numero(6);
elsif (ie_regra_w = 'DS' and (dt_nascimento_p IS NOT NULL AND dt_nascimento_p::text <> '')) then
	ds_senha_w := replace(dt_nascimento_p,'/','');
end if;

return ds_senha_w;

end;
