CREATE OR REPLACE FUNCTION obter_senha_prescr_proc (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w		bigint;
cd_senha_w		varchar(20);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_medica
	where	nr_atendimento = nr_atendimento_p;
end if;

if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
	select	max(cd_senha)
	into STRICT	cd_senha_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_w;
end if;

return	coalesce(cd_senha_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
