CREATE OR REPLACE FUNCTION obter_senha_retorno (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

cd_senha_w		varchar(20);
dt_periodo_inicial_w	timestamp;
nr_atendimento_w		bigint;
cd_convenio_w		bigint;


BEGIN 
 
select	max(dt_periodo_inicial), 
	max(nr_atendimento), 
	max(cd_convenio_parametro) 
into STRICT	dt_periodo_inicial_w, 
	nr_atendimento_w, 
	cd_convenio_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
select 	max(cd_senha) 
into STRICT	cd_senha_w 
from	atend_categoria_convenio 
where	nr_atendimento		= nr_atendimento_w 
and	cd_convenio		= cd_convenio_w 
and	dt_periodo_inicial_w	between coalesce(dt_inicio_vigencia, dt_periodo_inicial_w) and trunc(coalesce(dt_final_vigencia, dt_periodo_inicial_w)) + 89399/89400;
 
if (coalesce(cd_senha_w,'-1')	= '-1') then 
 
	select 	max(cd_senha) 
	into STRICT	cd_senha_w 
	from	atend_categoria_convenio 
	where	nr_seq_interno	= obter_atecaco_atendimento(nr_atendimento_w);
 
end if;
 
return	cd_senha_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
