SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_sequencia_adep_cpoe ( nr_seq_cpoe_p bigint, nr_prescricao_p bigint, cd_item_p text, ie_tipo_item_p text) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w	numeric(20);


BEGIN

if (ie_tipo_item_p in ('D')) then

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_dieta
		where	nr_prescricao 	  = nr_prescricao_p
		and		nr_seq_dieta_cpoe = nr_seq_cpoe_p
		and		cd_dieta          = cd_item_p;

elsif (ie_tipo_item_p in ('J')) then

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	rep_jejum
		where	nr_prescricao 	  = nr_prescricao_p
		and		nr_seq_dieta_cpoe = nr_seq_cpoe_p;

elsif (ie_tipo_item_p in ('LD')) then

		select	max(a.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_leite_deriv a,
				prescr_material b
		where	a.nr_sequencia	      = b.nr_seq_leite_deriv
		and		a.nr_prescricao	      = b.nr_prescricao
		and		a.nr_prescricao 	  = nr_prescricao_p
		and		a.nr_seq_dieta_cpoe   = nr_seq_cpoe_p
		and		b.cd_material         = cd_item_p;

elsif (ie_tipo_item_p in ('SOL')) then

		select	max(a.nr_seq_solucao)
		into STRICT	nr_sequencia_w
		from	prescr_solucao a,
				prescr_material b
		where	a.nr_prescricao		= b.nr_prescricao
		and		a.nr_seq_solucao	= b.nr_sequencia_solucao
		and		a.nr_prescricao     = nr_prescricao_p
		and		b.nr_seq_mat_cpoe   = nr_seq_cpoe_p;

elsif (ie_tipo_item_p in ('SNE', 'S')) then

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_material
		where	nr_prescricao 	  = nr_prescricao_p
		and		nr_seq_dieta_cpoe = nr_seq_cpoe_p
		and		cd_material       = cd_item_p;

elsif (ie_tipo_item_p in ('M','MAT')) then

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_material
		where	nr_prescricao 	= nr_prescricao_p
		and		nr_seq_mat_cpoe = nr_seq_cpoe_p
		and		cd_material     = cd_item_p;

elsif (ie_tipo_item_p in ('P', 'HM')) then

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_procedimento
		where	nr_prescricao 	 = nr_prescricao_p
		and		nr_seq_proc_cpoe = nr_seq_cpoe_p
		and		cd_procedimento  = cd_item_p;

elsif (ie_tipo_item_p in ('O')) then

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_gasoterapia
		where	nr_prescricao 	 = nr_prescricao_p
		and		nr_seq_gas_cpoe  = nr_seq_cpoe_p
		and		nr_seq_gas       = cd_item_p;

elsif (ie_tipo_item_p in ('R')) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	prescr_recomendacao
	where	nr_prescricao 	 = nr_prescricao_p
	and		nr_seq_rec_cpoe  = nr_seq_cpoe_p
	and		coalesce(cd_recomendacao, 999999)  = coalesce(cd_item_p, 999999);

elsif (ie_tipo_item_p in ('DI')) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	hd_prescricao
	where	nr_prescricao 	     = nr_prescricao_p
	and		nr_seq_dialise_cpoe  = nr_seq_cpoe_p;

elsif (ie_tipo_item_p in ('NAN', 'NPN', 'NPP')) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	nut_pac
	where	nr_prescricao 	     = nr_prescricao_p
	and		nr_seq_npt_cpoe  	= nr_seq_cpoe_p;

end if;

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_sequencia_adep_cpoe ( nr_seq_cpoe_p bigint, nr_prescricao_p bigint, cd_item_p text, ie_tipo_item_p text) FROM PUBLIC;

