CREATE OR REPLACE FUNCTION obter_sequencia_amostra (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_amostra_p bigint, ie_opcao_p bigint ) RETURNS varchar AS $body$
DECLARE


resultado_w	varchar(40);
nr_amostra_w	integer;


BEGIN

select count(*)
into STRICT nr_amostra_w
from prescr_proc_material
where nr_sequencia <= nr_seq_amostra_p
and nr_prescricao = nr_prescricao_p
and nr_seq_material = nr_seq_material_p;

resultado_w := nr_amostra_w;

return resultado_w;

end;
$body$
LANGUAGE PLPGSQL
