CREATE OR REPLACE FUNCTION obter_sequencia_lote ( nr_prescricao_p bigint, nr_seq_mat_hor_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_lote_w	bigint;


BEGIN


select 	max(a.nr_sequencia)
into STRICT	nr_seq_lote_w
from 	ap_lote		a,
	ap_lote_item	b,
	prescr_mat_hor	c,
	prescr_medica	d
where	a.nr_sequencia	 = b.nr_seq_lote
and 	b.nr_seq_mat_hor = c.nr_sequencia
and 	d.nr_prescricao  = a.nr_prescricao
and 	c.nr_prescricao	 = nr_prescricao_p
and 	c.nr_sequencia	 = nr_seq_mat_hor_p
and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
and	coalesce(c.ie_situacao,'A') <> 'I';

return	nr_seq_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
