CREATE OR REPLACE FUNCTION obter_sequencia_problema ( nr_seq_problema_p bigint, nr_seq_problema_sup_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_w					bigint;

cd_pessoa_profissional_w	pessoa_fisica.cd_pessoa_fisica%type;
cd_especialidade_w			especialidade_medica.cd_especialidade%type;


BEGIN

if (nr_seq_problema_p IS NOT NULL AND nr_seq_problema_p::text <> '') then

	Select  max(obter_pf_usuario(nm_usuario_p,'C'))
	into STRICT	cd_pessoa_profissional_w
	;

	Select  max(obter_especialidade_medico(cd_pessoa_profissional_w,'C'))
	into STRICT	cd_especialidade_w
	;

	if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then

		if (nr_seq_problema_sup_p IS NOT NULL AND nr_seq_problema_sup_p::text <> '') then

			Select	max(nr_seq_apresentacao)
			into STRICT	nr_seq_w
			from	lista_problema_seq_esp
			where	nr_seq_problema = nr_seq_problema_p
			and		nr_seq_problema_sup = nr_seq_problema_sup_p
			and		cd_especialidade = cd_especialidade_w;

		else

			Select	max(nr_seq_apresentacao)
			into STRICT	nr_seq_w
			from	lista_problema_seq_esp
			where	nr_seq_problema = nr_seq_problema_p
			and		coalesce(nr_seq_problema_sup::text, '') = ''
			and		cd_especialidade = cd_especialidade_w;

		end if;


	end if;

end if;

return nr_seq_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
