SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_sequencia_reapresentacao (nr_sequencia_p bigint, nr_seq_conpaci_ret_p bigint, dt_refencia_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_reapresentacao_w	integer;
qt_iguais_w		integer;
qt_iguais_dia_w		integer;
dt_historico_w		timestamp;
ds_retorno_w		varchar(255) := '';
ie_acao_w		varchar(255) := '';


BEGIN

select	max(obter_descricao_padrao('HIST_AUDIT_CONTA_PACIENTE', 'IE_ACAO', nr_seq_hist_audit))
into STRICT	ie_acao_w
from	conta_paciente_ret_hist
where	nr_sequencia		= nr_sequencia_p;

if (ie_acao_w = '2') then

	select	coalesce(count(*), 0)
	into STRICT	qt_reapresentacao_w
	from	conta_paciente_ret_hist
	where	nr_seq_conpaci_ret	= nr_seq_conpaci_ret_p
	and	(dt_reapresentacao IS NOT NULL AND dt_reapresentacao::text <> '')
	and	obter_descricao_padrao('HIST_AUDIT_CONTA_PACIENTE', 'IE_ACAO', nr_seq_hist_audit) = '2'
	and	trunc(dt_reapresentacao,'dd') < trunc(dt_refencia_p,'dd');

	select	coalesce(count(*), 0)
	into STRICT	qt_iguais_w
	from	conta_paciente_ret_hist
	where	nr_seq_conpaci_ret	= nr_seq_conpaci_ret_p
	and	(dt_reapresentacao IS NOT NULL AND dt_reapresentacao::text <> '')
	and	obter_descricao_padrao('HIST_AUDIT_CONTA_PACIENTE', 'IE_ACAO', nr_seq_hist_audit) = '2'
	and	nr_sequencia		<> nr_sequencia_p
	and	trunc(dt_reapresentacao,'dd') = trunc(dt_refencia_p,'dd');

	if (coalesce(qt_iguais_w,0)	> 0) then
		select	max(dt_historico)
		into STRICT	dt_historico_w
		from	conta_paciente_ret_hist
		where	nr_sequencia	= nr_sequencia_p;

		select	coalesce(count(*), 0)
		into STRICT	qt_iguais_dia_w
		from	conta_paciente_ret_hist
		where	nr_seq_conpaci_ret	= nr_seq_conpaci_ret_p
		and	(dt_reapresentacao IS NOT NULL AND dt_reapresentacao::text <> '')
		and	obter_descricao_padrao('HIST_AUDIT_CONTA_PACIENTE', 'IE_ACAO', nr_seq_hist_audit) = '2'
		and	nr_sequencia		<> nr_sequencia_p
		and	dt_historico 		< dt_historico_w;

		if (qt_reapresentacao_w	= 0) then
			qt_reapresentacao_w	:= 1;
		end if;

		qt_reapresentacao_w	:= qt_reapresentacao_w + qt_iguais_dia_w;

		if (coalesce(qt_iguais_dia_w,0)	= 0) then
			ds_retorno_w	:= (qt_reapresentacao_w + 1) || 'ª';
		else
			ds_retorno_w	:= (qt_reapresentacao_w || 'ª');
		end if;

	else
		ds_retorno_w	:= (qt_reapresentacao_w + 1) || 'ª';
	end if;

else
	ds_retorno_w	:= '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
