CREATE OR REPLACE FUNCTION obter_seq_acao_exec_teste ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*	IE_OPCAO_P
	C - Sequência de apresentação do caso de testes
	I - Sequência de apresentação do item	*/
ds_retorno_w		integer;
nr_seq_apres_caso_w	integer;
nr_seq_apres_item_w	integer;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	select	a.nr_seq_apres,
		b.nr_seq_apres
	into STRICT	nr_seq_apres_caso_w,
		nr_seq_apres_item_w
	from	teste_soft_exec_acao c,
		teste_caso_teste_item b,
		teste_caso_teste a
	where	a.nr_sequencia = b.nr_seq_caso_teste
	and	b.nr_sequencia = c.nr_seq_item
	and	c.nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'C') then
		ds_retorno_w := nr_seq_apres_caso_w;
	elsif (ie_opcao_p = 'I') then
		ds_retorno_w := nr_seq_apres_item_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
