CREATE OR REPLACE FUNCTION obter_seq_apresentacao_exame ( nr_seq_protocolo_p bigint, nr_seq_exame_p bigint, nr_seq_grupo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_apresentacao_w	bigint := null;


BEGIN

if (nr_seq_protocolo_p > 0) then
	if (nr_seq_exame_p > 0) then
		select 	max(nr_seq_apresentacao)
		into STRICT	nr_seq_apresentacao_w
		from   	pep_exame_grid_item
		where	nr_seq_pep_exame_grid = nr_seq_protocolo_p
		and	nr_seq_exame = nr_seq_exame_p;
	elsif (nr_seq_grupo_p > 0) then
		select 	max(nr_seq_apresentacao)
		into STRICT	nr_seq_apresentacao_w
		from   	pep_exame_grid_item
		where	nr_seq_pep_exame_grid = nr_seq_protocolo_p
		and	nr_seq_grupo = nr_seq_grupo_p;
	end if;
end if;

return	nr_seq_apresentacao_w;

end;
$body$
