CREATE OR REPLACE FUNCTION obter_seq_apresent_conta ( nr_atendimento_p bigint, ie_tipo_atend_conta_p bigint, cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

		 
nr_retorno_w		bigint;	
qt_incremento_w		bigint := 0;	
nr_seq_inicial_w	bigint := 0;	
 
C01 CURSOR FOR 
	SELECT	qt_incremento, 
		nr_seq_inicial 
	from	conv_regra_seq_conta	 
	where	cd_convenio 						= cd_convenio_p 
	and	cd_estabelecimento 					= cd_estabelecimento_p 
	and	coalesce(ie_tipo_atend_conta,coalesce(ie_tipo_atend_conta_p,0)) 	= coalesce(ie_tipo_atend_conta_p,0) 
	and	ie_situacao = 'A' 
	order by	cd_convenio, 
			coalesce(ie_tipo_atend_conta,0);


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	qt_incremento_w, 
    nr_seq_inicial_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	coalesce(max(nr_seq_apresentacao) + qt_incremento_w,nr_seq_inicial_w) 
	into STRICT	nr_retorno_w 
	from	conta_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and	cd_estabelecimento 					= cd_estabelecimento_p 
	and	cd_convenio_parametro 					= cd_convenio_p 
	and	coalesce(ie_tipo_atend_conta,coalesce(ie_tipo_atend_conta_p,0)) 	= coalesce(ie_tipo_atend_conta_p,0);
	 
	end;
end loop;
close C01;
	 
return	nr_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
