CREATE OR REPLACE FUNCTION obter_seq_apresent_peca_princ (nr_seq_peca_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_peca_princ_w	bigint;
ds_retorno_w		varchar(255);

BEGIN

select 	max(nr_seq_peca)
into STRICT	nr_seq_peca_princ_w
from	prescr_proc_peca
where	nr_sequencia  = nr_seq_peca_p;


if (nr_seq_peca_princ_w IS NOT NULL AND nr_seq_peca_princ_w::text <> '') then

	select 	max(nr_seq_apresent)
	into STRICT	ds_retorno_w
	from	prescr_proc_peca
	where	nr_sequencia = nr_seq_peca_princ_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
