CREATE OR REPLACE FUNCTION obter_seq_apres_discri (nr_seq_item_p bigint, nr_seq_fluxo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w	bigint;
nr_discriminadores_w	bigint;


BEGIN
	nr_discriminadores_w := 0;
	nr_retorno_w := 0;
	if (nr_seq_item_p > 0) then
		select count(*)
		into STRICT nr_discriminadores_w
		from manchester_flux_item_cli
		where nr_seq_discriminador = nr_seq_item_p
		and   nr_seq_fluxo = nr_seq_fluxo_p;

		if (nr_discriminadores_w > 0) then
			select max(nr_seq_apres)
			into STRICT nr_retorno_w
			from manchester_flux_item_cli
			where nr_seq_discriminador = nr_seq_item_p
			and   nr_seq_fluxo = nr_seq_fluxo_p;
		else
			select max(nr_seq_apresentacao)
			into STRICT nr_retorno_w
			from manchester_fluxograma_item
			where nr_seq_item = nr_seq_item_p
			and   nr_seq_fluxo = nr_seq_fluxo_p;
		end if;
	end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
