CREATE OR REPLACE FUNCTION obter_seq_apres_exame ( nr_seq_pedido_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_apresent_w	bigint;


BEGIN

if (nr_seq_pedido_p IS NOT NULL AND nr_seq_pedido_p::text <> '') then

	select (coalesce(max(nr_seq_apresent),0) + 1)
	into STRICT	nr_seq_apresent_w
	from	med_ped_exame_cod
	where	nr_seq_pedido = nr_seq_pedido_p;

end if;

return nr_seq_apresent_w;

end;
$body$
