CREATE OR REPLACE FUNCTION obter_seq_apres_tipo_item (nr_seq_item_p bigint) RETURNS bigint AS $body$
DECLARE

					 
nr_resultado_w		bigint;


BEGIN 
 
select	max(a.nr_seq_apres) nr_seq_apresent 
into STRICT	nr_resultado_w 
from	PE_TIPO_ITEM a, 
	PE_ITEM_TIPO_ITEM b 
where	b.nr_seq_tipo_item = a.nr_sequencia	 
and	b.nr_seq_item = nr_seq_item_p;
 
return	nr_resultado_w;
 
end;
$body$
