CREATE OR REPLACE FUNCTION obter_seq_aprovacoes_ordem ( nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_lista_aprovacoes_w		varchar(255);
nr_seq_aprovacao_w		bigint;

c01 CURSOR FOR
SELECT	distinct nr_seq_aprovacao
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	(nr_seq_aprovacao IS NOT NULL AND nr_seq_aprovacao::text <> '');

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_aprovacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_lista_aprovacoes_w := substr(ds_lista_aprovacoes_w || nr_seq_aprovacao_w || ', ',1,255);
	end;
end loop;
close C01;

ds_lista_aprovacoes_w := substr(ds_lista_aprovacoes_w,1,length(ds_lista_aprovacoes_w)-2);

return	ds_lista_aprovacoes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
