CREATE OR REPLACE FUNCTION obter_seq_area_imagem_odonto ( nr_seq_imagem_p bigint, nr_seq_local_p text, nr_seq_dente_p text, nr_seq_sextante_p text, nr_seq_arcada_p text, nr_seq_tratamento_p text, nr_seq_face_p text, ie_raiz_p text, ie_prot_sup text, ie_prot_inf text ) RETURNS varchar AS $body$
DECLARE


		c01 CURSOR FOR
		SELECT	nr_seq_area
		from	area_imagem_item
		where	nr_seq_area in (SELECT	nr_sequencia
					from	area_imagem
					where	nr_seq_img = nr_seq_imagem_p)
		and (coalesce(nr_seq_local_p::text, '') = '' or nr_seq_local = nr_seq_local_p)
		and (coalesce(nr_seq_dente_p::text, '') = '' or nr_seq_dente = nr_seq_dente_p)
		and (coalesce(nr_seq_sextante_p::text, '') = '' or nr_seq_sextante = nr_seq_sextante_p)
		and (coalesce(nr_seq_arcada_p::text, '') = '' or nr_seq_arcada = nr_seq_arcada_p)
		and (coalesce(nr_seq_tratamento_p::text, '') = '' or nr_seq_tratamento = nr_seq_tratamento_p)
		and	((coalesce(nr_seq_face_p::text, '') = '' and coalesce(nr_seq_face::text, '') = '') or nr_seq_face = nr_seq_face_p)
		and (coalesce(ie_raiz,'N') = coalesce(ie_raiz_p,'N'));

		c02 CURSOR FOR
		SELECT	nr_seq_area
		from	area_imagem_item
		where	nr_seq_area in (SELECT	nr_sequencia
					from	area_imagem
					where	nr_seq_img = nr_seq_imagem_p)
		and	nr_seq_local = 'D'
		and	obter_se_dente_protese_odont(nr_seq_area, null, ie_prot_sup, ie_prot_inf) = 'S';

		nr_seq_area_w	bigint;
		ds_retorno_w	varchar(1000);


BEGIN

	if (nr_seq_local_p <> 'PROT') then

		open c01;
		loop
		fetch c01 into
				nr_seq_area_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
				if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
					ds_retorno_w := ds_retorno_w || ',';
				end if;
				ds_retorno_w := ds_retorno_w || nr_seq_area_w;
			end;
		end loop;
		close c01;

	else
		open c02;
		loop
		fetch c02 into
				nr_seq_area_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
				if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
					ds_retorno_w := ds_retorno_w || ',';
				end if;
				ds_retorno_w := ds_retorno_w || nr_seq_area_w;
			end;
		end loop;
		close c02;


	end if;

	return ds_retorno_w;

END;
$body$
