CREATE OR REPLACE FUNCTION obter_seq_assinatura_executor ( nr_prescricao_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_retorno_w		bigint;
nr_seq_assinatura_w	bigint;
nr_sequencia_w		bigint;
/* --------- SequÃªncias de assinaturas ---------
	PSE - prescr_solucao_evento
	PMA - presc_mat_alteracao
   ---------------------------------------------
*/
c01 CURSOR FOR
	SELECT	nr_sequencia
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_seq_assinatura_w := null;
		if (ie_opcao_p = 'PMA') then
			select	min(nr_seq_assinatura)
			into STRICT	nr_seq_assinatura_w
			from	prescr_mat_alteracao
			where	nr_prescricao	= nr_prescricao_p
			and	nr_seq_prescricao	= nr_sequencia_w
			and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
		elsif (ie_opcao_p = 'PSE') then
			select	min(nr_seq_assinatura)
			into STRICT	nr_seq_assinatura_w
			from	prescr_solucao_evento
			where	nr_prescricao	= nr_prescricao_p
			and	nr_seq_material	= nr_sequencia_w
			and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
		end if;

		if (nr_seq_assinatura_w IS NOT NULL AND nr_seq_assinatura_w::text <> '') then
			nr_retorno_w := nr_seq_assinatura_w;
		end if;
		end;
	end loop;
	close c01;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
