CREATE OR REPLACE FUNCTION obter_seq_bem ( nr_seq_bem_p bigint, cd_bem_p text, cd_estabelecimento_p bigint, cd_empresa_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_bem_w		pat_bem.nr_sequencia%type;


BEGIN



if (coalesce(nr_seq_bem_p,0) = 0) then
	begin
	begin
		select	nr_sequencia
		into STRICT	nr_seq_bem_w
		from	pat_bem a
		where	a.cd_bem = cd_bem_p
		and	a.ie_tipo_valor = 'O'
		and	cd_estabelecimento = cd_estabelecimento_p;
	exception
	when no_data_found then
		begin
			select	nr_sequencia
			into STRICT	nr_seq_bem_w
			from	pat_bem a
			where	a.cd_bem = cd_bem_p
			and	a.ie_tipo_valor = 'O'
			and	obter_empresa_estab(a.cd_estabelecimento) = cd_empresa_p;
		exception
		when no_data_found then
			begin
			nr_seq_bem_w := null;
			/* Bem não cadastrado no sistema */

			end;
		when too_many_rows then
			begin
			nr_seq_bem_w := null;
			/* Mais de um bem com o mesmo código na empresa mas em estabelecimentos diferentes do estabelecimento do inventário */

			end;
		end;
	when too_many_rows then
		begin
		nr_seq_bem_w := null;
		/* Mais de um bem com o mesmo código no estabelecimento do inventário */

		end;
	end;	
	end;
else
	begin
	nr_seq_bem_w := nr_seq_bem_p;
	end;
end if;

return nr_seq_bem_w;

end;
$body$
