CREATE OR REPLACE FUNCTION obter_seq_cd_banco () RETURNS bigint AS $body$
DECLARE


cd_banco_w     banco.cd_banco%type;
cd_banco_seq_w banco.cd_banco%type;
count_w        banco.cd_banco%type;


BEGIN

select min(coalesce(cd_banco,1))
into STRICT cd_banco_seq_w
from banco;

count_w := 0;

while count_w <> -1 loop
    begin
        
        cd_banco_w := cd_banco_seq_w;

        select coalesce(max(cd_banco),-1)
        into STRICT count_w
        from banco
        where cd_banco = cd_banco_w;

        cd_banco_seq_w := cd_banco_seq_w + 1;

    end;
end loop;

return cd_banco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
