CREATE OR REPLACE FUNCTION obter_seq_checklist_processo (nr_seq_check_item_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_checklist_w 	bigint;


BEGIN

if (nr_seq_check_item_p IS NOT NULL AND nr_seq_check_item_p::text <> '') then
	SELECT	MAX(b.nr_sequencia)
	into STRICT	nr_seq_checklist_w
	FROM  	CHECKLIST_PROCESSO_ITEM a,
			CHECKLIST_PROCESSO b
	WHERE	b.nr_sequencia = a.nr_seq_checklist
	AND		a.nr_sequencia = nr_seq_check_item_p;
end if;

return nr_seq_checklist_w;

end;
$body$
LANGUAGE PLPGSQL
