CREATE OR REPLACE FUNCTION obter_seq_cons_sinais_vitais ( nr_atendimento_p bigint, nm_tabela_p text, nm_atributo_p text) RETURNS bigint AS $body$
DECLARE

 
cd_empresa_w			smallint;
cd_estabelecimento_w		smallint;
ds_retorno_w			bigint := 0;

 

BEGIN 
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
select	cd_empresa 
into STRICT	cd_empresa_w 
from	estabelecimento 
where	cd_estabelecimento	= cd_estabelecimento_w;
 
 
select	coalesce(coalesce(max(a.nr_seq_consulta),max(b.nr_seq_consulta)),0) 
into STRICT	ds_retorno_w 
from	pep_inf_regra c, 
	pep_informacao b, 
	pep_dest_inf a 
where	a.nm_tabela		= nm_tabela_p 
and	a.nm_atributo		= nm_atributo_p 
and	c.cd_empresa		= cd_empresa_w 
and	coalesce(c.cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w 
and	a.nr_seq_inf		= b.nr_sequencia 
and	b.nr_sequencia		= c.nr_seq_inf 
and (a.nr_sequencia		= c.nr_seq_dest_inf or coalesce(c.nr_seq_dest_inf::text, '') = '') 
and	c.ie_informacao		= 'Usuario';
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
