SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_seq_conta_atend ( nr_atendimento_p bigint, nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_interno_conta_w		bigint;
qt_contas_w			bigint	:= 0;
				
				 
C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_atendimento	 		= nr_atendimento_p 
	and	obter_tipo_convenio(cd_convenio_parametro) <> 1 
	order by 1;
				

BEGIN 
 
 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	qt_contas_w		:= qt_contas_w + 1;
	if (nr_interno_conta_w	= nr_interno_conta_p) then 
		exit;
	end if;
	 
	end;
end loop;
close C01;
 
return	qt_contas_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
