CREATE OR REPLACE FUNCTION obter_seq_cpoe_evento ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text) RETURNS bigint AS $body$
DECLARE

  ie_retorno_w prescr_material.nr_seq_mat_cpoe%type := 0;

BEGIN

  if ((nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (ie_tipo_item_p IN ('M', 'IAH'))) then

    select coalesce(max(a.nr_seq_mat_cpoe), 0)
      into STRICT ie_retorno_w
      from prescr_material a
     where a.nr_prescricao = nr_prescricao_p
       and a.nr_sequencia = nr_seq_item_p;

  end if;

return ie_retorno_w;

end;
$body$
