CREATE OR REPLACE FUNCTION obter_seq_doacao_sorologia (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

 
nr_seq_doacao_w	bigint;

BEGIN
 
SELECT 	max(x.nr_sequencia) 
into STRICT	nr_seq_doacao_w	 
FROM  san_doacao x 
WHERE  x.cd_pessoa_fisica IN (	SELECT	a.cd_pessoa_fisica 
				FROM  	san_transfusao c, 
					san_producao b, 
					san_doacao a 
				WHERE 	a.nr_sequencia = b.nr_seq_doacao 
				AND	c.nr_sequencia = b.nr_seq_transfusao 
				AND 	obter_receptor_transfusao(c.nr_sequencia, 'C') = cd_pessoa_fisica_p 
				AND	a.nr_sequencia <> x.nr_sequencia );
						 
return	nr_seq_doacao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
