CREATE OR REPLACE FUNCTION obter_seq_exec_evento_cirurgia (nr_cirurgia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_evento_w		bigint;
nr_seq_execucao_w	bigint;


BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	begin
	select	max(nr_seq_execucao)
	into STRICT	nr_seq_execucao_w
	from	evento_cirurgia_paciente a,
		evento_cirurgia b
	where	a.nr_cirurgia = nr_cirurgia_p
	and	b.nr_sequencia = a.nr_seq_evento
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	exists (SELECT	1
			from	evento_cirurgia b
			where	b.nr_sequencia = a.nr_seq_evento
			and	(b.ie_evento_painel IS NOT NULL AND b.ie_evento_painel::text <> '')
			and	coalesce(b.ie_situacao,'A') = 'A');


	/*if	(nr_seq_evento_w <> 0) then
		select	max(nr_seq_execucao)
		into	nr_seq_execucao_w
		from	evento_cirurgia
		where	nr_sequencia = nr_seq_evento_w
		and	ie_evento_painel is not null;
	end if;*/
	end;
end if;

return nr_seq_execucao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
