CREATE OR REPLACE FUNCTION obter_seq_hor_etapa_sol ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_sol_hor_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_seq_hor_w prescr_mat_hor.nr_sequencia%type;


BEGIN

nr_seq_hor_w := null;

if ((nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and
	(nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and
	nr_etapa_sol_hor_p > 0) then

	select 	max(nr_sequencia)
	into STRICT 	nr_seq_hor_w
	from 	prescr_mat_hor
	where 	nr_prescricao 	= nr_prescricao_p
	and 	nr_seq_solucao	= nr_seq_solucao_p
	and 	nr_etapa_sol	= nr_etapa_sol_hor_p
	and		coalesce(ie_horario_especial, 'N') = 'N';

end if;

return nr_seq_hor_w;

end;
$body$
