CREATE OR REPLACE FUNCTION obter_seq_interno_prescr ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_propaci_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_interno_w	bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then
	select coalesce(max(nr_seq_interno),0)
	into STRICT	nr_seq_interno_w
	from prescr_procedimento
	where nr_prescricao	= nr_prescricao_p
	  and nr_sequencia	= nr_seq_prescr_p;
elsif (nr_seq_propaci_p IS NOT NULL AND nr_seq_propaci_p::text <> '') then
	select coalesce(max(b.nr_seq_interno),0)
	into STRICT	nr_seq_interno_w
	from	prescr_procedimento b,
		procedimento_paciente a
	where b.nr_prescricao	= a.nr_prescricao
	  and b.nr_sequencia		= a.nr_sequencia_prescricao
	  and a.nr_sequencia		= nr_seq_propaci_p;
end if;

return nr_seq_interno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
